INSERT INTO apt.atbd_versions (major, minor, atbd_id, document, sections_completed, published_by, published_at, created_by, created_at, last_updated_by, last_updated_at, doi, citation, owner, authors, reviewers, status, journal_status, publication_checklist, keywords) VALUES (1, 0, 30, '{"scientific_theory": {"children": [{"type": "p", "children": [{"text": "I love LaTex. x^2 "}]}, {"type": "p", "children": [{"text": "why does this work like this? e=mc^2"}]}, {"type": "equation", "children": [{"text": "x^2"}]}, {"type": "p", "children": [{"text": ""}]}, {"type": "p", "children": [{"text": ""}]}]}, "scientific_theory_assumptions": {"children": [{"type": "p", "children": [{"text": ""}]}]}, "mathematical_theory": {"children": [{"type": "p", "children": [{"text": ""}]}]}, "mathematical_theory_assumptions": {"children": [{"type": "p", "children": [{"text": ""}]}]}, "algorithm_input_variables": [], "algorithm_input_variables_caption": "", "algorithm_output_variables": [], "algorithm_output_variables_caption": "", "publication_references": []}', '{"scientific_theory": "incomplete", "mathematical_theory": "incomplete", "input_variables": "incomplete", "output_variables": "incomplete"}', NULL, NULL, 'a5317c03-765f-4702-9007-bf9a7fae26b6', '2022-01-31 17:22:32.701375+00', 'a5317c03-765f-4702-9007-bf9a7fae26b6', '2022-01-31 17:27:10.437198+00', NULL, '{}', 'a5317c03-765f-4702-9007-bf9a7fae26b6', '{}', '{}', 'DRAFT', NULL, '{"journal_editor": "Chelle Gentemann"}', '{}');
INSERT INTO apt.atbd_versions (major, minor, atbd_id, document, sections_completed, published_by, published_at, created_by, created_at, last_updated_by, last_updated_at, doi, citation, owner, authors, reviewers, status, journal_status, publication_checklist, keywords) VALUES (1, 0, 4, '{"introduction": {"children": [{"type": "p", "children": [{"text": "This User Guide/ATBD references the Provisional HLS Version 1.5 data, which have not been validated for their science quality and should not be used in science research or applications.", "bold": true}]}, {"type": "p", "children": [{"text": "Many land monitoring applications require more frequent observations than can be obtained from a single \u201cLandsat-class\u201d sensor. Examples of these applications include crop type and condition monitoring, vegetation phenology, disaster response, and surface water quality. These and numerous other applications all require near-daily imagery at medium spatial resolution."}]}, {"type": "p", "children": [{"text": "Since trying to achieve near-daily observation frequency from a single, well-calibrated satellite system may be prohibitively expensive, a reasonable alternative is to combine data from multiple international sources. The Harmonized Landsat/Sentinel-2 (HLS) Project supports this goal by generating a harmonized surface reflectance product from Landsat 8 and Sentinel-2 inputs. Specifically, by \u201charmonized\u201d we mean that the products are:"}]}, {"type": "ul", "children": [{"type": "li", "children": [{"type": "p", "children": [{"text": "Gridded to a common pixel resolution, projection, and spatial extent (i.e., tile);"}]}]}]}, {"type": "ul", "children": [{"type": "li", "children": [{"type": "p", "children": [{"text": "Atmospherically corrected to surface reflectance using a common radiative transfer algorithm ;"}]}]}]}, {"type": "ul", "children": [{"type": "li", "children": [{"type": "p", "children": [{"text": "Normalized to a common nadir-view geometry via Bi-directional Reflectance Distribution Function (BRDF) estimation;"}]}]}]}, {"type": "ul", "children": [{"type": "li", "children": [{"type": "p", "children": [{"text": "Adjusted to represent the response from a common spectral bandpass."}]}]}]}, {"type": "p", "children": [{"text": "HLS generates two tiled image products at 30-meter spatial resolution, \u201cL30\u201d and \u201cS30\u201d, derived from individual Landsat-8 and Sentinel-2, acquisitions, respectively. These products constitute a \u201cstackable\u201d Analysis Ready Data (ARD) format, such that a user may examine any given pixel through time, and treat the near-daily reflectance time series as though it came from a single sensor."}]}]}, "historical_perspective": {"children": [{"type": "p", "children": [{"text": "The Landsat series of satellites have provided land imagery of the globe since 1972. Each satellite provides 16-day repeat coverage of global land areas. Landsat 8, launched in February 2013, introduced significant improvements in the number of spectral bands and the radiometric quality of the data. Landsat 8 includes two instruments: the Operational Land Imager (OLI) covering the visible/near/shortwave reflective bands, and the Thermal Infrared Sensor (TIRS) covering the longwave (thermal) infrared bands. The European Sentinel-2 satellites (part of the Copernicus environmental monitoring service) provide a similar type of imagery to Landsat, but with additional spectral bands and higher resolution via its Multispectral Imager (MSI) instrument. Sentinel-2a was launched in 2015, and Sentinel-2b in 2017. Each Sentinel-2 satellite provides global land coverage every 10 days, or every 5 days for the two-satellite constellation. Combining the data from Landsat 8, Sentinel-2a, and Sentinel-2b provides some 100 observations per year for equatorial regions, and over 200 acquisitions per year in mid-latitudes and regions of orbit overlap (Li and Roy, 2017). "}]}, {"type": "p", "children": [{"text": "While similar, Landsat 8 and Sentinel-2 are not identical. The table below lists some of the differences between the systems. HLS attempts to adjust each product to create a harmonized \u201cLandsat like\u201d reflectance image. "}]}, {"type": "p", "children": [{"text": ""}]}, {"type": "table-block", "children": [{"type": "table", "children": [{"type": "tr", "children": [{"type": "td", "children": [{"type": "p", "children": [{"text": ""}]}]}, {"type": "td", "children": [{"type": "p", "children": [{"text": "Landsat 8", "bold": true}]}]}, {"type": "td", "children": [{"type": "p", "children": [{"text": "Sentinel-2", "bold": true}]}]}]}, {"type": "tr", "children": [{"type": "td", "children": [{"type": "p", "children": [{"text": "Orbital altitude"}]}]}, {"type": "td", "children": [{"type": "p", "children": [{"text": "705 km"}]}]}, {"type": "td", "children": [{"type": "p", "children": [{"text": "786 km"}]}]}]}, {"type": "tr", "children": [{"type": "td", "children": [{"type": "p", "children": [{"text": "Equatorial crossing time (MLT)"}]}]}, {"type": "td", "children": [{"type": "p", "children": [{"text": "10:00 am"}]}]}, {"type": "td", "children": [{"type": "p", "children": [{"text": "10:30 am"}]}]}]}, {"type": "tr", "children": [{"type": "td", "children": [{"type": "p", "children": [{"text": "Image swath $\\backslash$ view angle field of view"}]}]}, {"type": "td", "children": [{"type": "p", "children": [{"text": "180km $\\backslash$ 15 deg FOV"}]}]}, {"type": "td", "children": [{"type": "p", "children": [{"text": "290 km $\\backslash$ 20.1 deg FOV"}]}]}]}, {"type": "tr", "children": [{"type": "td", "children": [{"type": "p", "children": [{"text": "Spatial resolution (VIS/SWIR/TIR)"}]}]}, {"type": "td", "children": [{"type": "p", "children": [{"text": "30/30/100m"}]}]}, {"type": "td", "children": [{"type": "p", "children": [{"text": "10/20m (no TIR)"}]}]}]}, {"type": "tr", "children": [{"type": "td", "children": [{"type": "p", "children": [{"text": "Spectral bands"}]}]}, {"type": "td", "children": [{"type": "p", "children": [{"text": "9 VSWIR, 2 TIR"}]}]}, {"type": "td", "children": [{"type": "p", "children": [{"text": "12 VSWIR"}]}]}]}, {"type": "tr", "children": [{"type": "td", "children": [{"type": "p", "children": [{"text": "Ground track repeat"}]}]}, {"type": "td", "children": [{"type": "p", "children": [{"text": "16 days"}]}]}, {"type": "td", "children": [{"type": "p", "children": [{"text": "10 days (per platform)"}]}]}]}]}, {"type": "caption", "children": [{"text": ""}]}]}]}, "additional_information": {"children": [{"type": "p", "children": [{"text": ""}]}]}, "scientific_theory": {"children": [{"type": "p", "children": [{"text": "HLS uses a processing chain involving several separate radiometric and geometric adjustments, with a goal of eliminating differences in retrieved surface reflectance arising solely from differences in instrumentation. The overall chain is shown in Fig. 1. Input data products from Landsat 8 (Collection 2 Level 1T top-of-atmosphere reflectance or top-of-atmosphere apparent temperature) and Sentinel-2 (L1C top-of-atmosphere reflectance) are ingested for HLS processing. A series of radiometric and geometric corrections are applied as described below to convert data to surface reflectance, adjust for BRDF differences, and adjust for spectral bandpass differences. "}]}, {"type": "p", "children": [{"text": "Three types of products are then generated: \u201cS10\u201d products \u2013 atmospherically corrected Sentinel-2 images in their native resolution and geometry; and the harmonized products \u201cS30\u201d and \u201cL30\u201d. These products have been radiometrically harmonized to the maximum extent, and then gridded to a common 30-meter UTM basis using the Sentinel-2 tile system. Note that S10 products are not normally archived. The S30 and L30 products are resampled as needed to a common 30-meter resolution UTM projection, and tiled using the Sentinel-2 Military Grid Reference System (MGRS) UTM grid."}]}, {"type": "p", "children": [{"text": "Below we describe the algorithms use for (1) atmospheric correction; (2) BRDF adjustment; (3) bandpass normalization; and (4) geometric processing. More detailed descriptions can be found in Claverie et al. (2018); we note below cases where algorithms have been changed or updated from that reference. "}]}, {"type": "p", "children": [{"text": ""}]}, {"type": "p", "children": [{"text": "1.  Atmospheric Correction"}]}, {"type": "p", "children": [{"text": "HLS relies on the operational LaSRC (Landsat Surface Reflectance Correction) algorithm for atmospherically correcting top-of-atmosphere reflectance to surface reflectance.  As described in Vermote et al., (2016), the LaSRC approach relies on the inversion of the relatively simple equation in the Lambertian case, with no adjacency effects that account for a simplified coupling of the absorption by atmospheric gases and scattering by molecules and aerosols as it is implemented in the 6SV radiative transfer code (Vermote et al. 1997b, Kotchenova et al. 2006):"}]}, {"type": "p", "children": [{"text": ""}]}, {"type": "equation", "children": [{"text": "\\rho_{TOA}\\left( \\theta_{S},\\theta_{\\nu}, \\psi, P, \\overbrace{\\tau_{A}, \\omega_{0},P_{A}}^{Aerosols},U_{H_{2}O}, U_{O_{3}}\\right) = Tg_{OG} \\left(m,P\\right)Tg_{O_{3}}\\left(m,U_{U_{O_{3}}}\\right) \\left[\\rho_{atm}\\left(\\theta_{S}, \\theta{\\nu},\\psi,P,\\tau_{A}, \\omega_{0},P_{A},U_{H_{2}O}\\right) + Tr_{atm}\\left(\\theta_{S}, \\theta{\\nu},\\psi,P,\\tau_{A}, \\omega_{0},P_{A}\\right)\\frac{\\rho_{s}}{1-S_{atm}\\left(P,\\tau_{A}, \\omega_{0},P_{A}\\right)\\rho_{s}}Tg_{H_{2}O}\\left(m,U_{U_{H_{2}O}}\\right)\\right]"}]}, {"type": "p", "children": [{"text": "where $\\rho$"}, {"text": "TOA", "subscript": true}, {"text": " is the reflectance at the top of the atmosphere, $\\rho$"}, {"text": "atm", "subscript": true}, {"text": " is the atmosphere intrinsic reflectance, $Tr$"}, {"text": "atm", "subscript": true}, {"text": " is the total atmosphere transmission (downward and upward), $S$"}, {"text": "atm", "subscript": true}, {"text": " is the atmosphere spherical albedo, and $\\rho$"}, {"text": "s", "subscript": true}, {"text": " is the surface reflectance to be retrieved by the atmospheric correction procedure: the geometric conditions are described by the solar zenith angle $qs$, the view zenith angle $qv$, and the relative azimuth $f$ (or the difference between the solar and view azimuth angles); $P$ is the pressure that influences the number of molecules and the concentration of absorbing gases in the atmosphere, $Tg$ designates the gaseous transmission by water vapor ($Tg$"}, {"text": "H2O", "subscript": true}, {"text": "), ozone ($Tg$"}, {"text": "O3", "subscript": true}, {"text": "), or other gases ($Tg$"}, {"text": "OG", "subscript": true}, {"text": "), $U$"}, {"text": "H2O", "subscript": true}, {"text": " is the integrated water vapor content, $U$"}, {"text": "O3", "subscript": true}, {"text": " is the integrated ozone content, and $m$ is the so-called \u201cair-mass\u201d computed as $1/\\cos(qs)+1/\\cos(qv)$; $t$"}, {"text": "A", "subscript": true}, {"text": ", $w$"}, {"text": "0", "subscript": true}, {"text": " and $P$"}, {"text": "A", "subscript": true}, {"text": " describe the aerosol properties and are spectrally dependent: $\\tau$"}, {"text": "a", "subscript": true}, {"text": " is the aerosol optical thickness, $w$"}, {"text": "0", "subscript": true}, {"text": " is the aerosol single scattering albedo, and $P$"}, {"text": "A", "subscript": true}, {"text": " is the aerosol phase function."}]}, {"type": "p", "children": [{"text": "The main limitation of 6SV is the plane parallel assumption for the atmosphere, which limits the quoted accuracy (0.4$%$) to Sun and view zenith angles lower than 75 degrees. In addition to this limitation, Equation [1] supposes that scattering and absorption could be decoupled, which is not true where both strong absorption and scattering regimes occur for example near strong water vapor absorption lines in the near-infrared (e.g. in MODIS band 18 and 19). "}]}, {"type": "p", "children": [{"text": "The atmospheric pressure $P$ is obtained from a combination of data available from a coarse-resolution (one deg., six hours time step) weather prediction model available from NCEP GDAS (2015), sea level pressure $P$"}, {"text": "sl", "subscript": true}, {"text": " and the altitude $z$ [km] given by a Digital Elevation Model at 0.05 degree resolution (ETOPO5, 1988) and computed as:"}]}, {"type": "p", "children": [{"text": ""}]}, {"type": "equation", "children": [{"text": "P = P_{sl}e^{-\\frac{z}{8}}"}]}, {"type": "p", "children": [{"text": "The pressure should be representative of the average atmospheric pressure along the path from the Sun to the target and back to the satellite for primary scattering, and along an even more complicated path for multiple scattering. "}]}, {"type": "p", "children": [{"text": "Gaseous absorption terms include ozone and water vapor. The ozone amount $U$"}, {"text": "O3", "subscript": true}, {"text": " is obtained via NCEP GDAS (2015) (at 1deg., 6 hours time step) via the ancillary information included in the MODIS surface reflectance Climate Modeling Grid (MOD09CMA). The surface reflectance Climate Modeling Grid (CMG) adopted a simple Geographic latitude/longitude projection at 0.05 degree ($\\sim$ 5.5 km). The water vapor is also extracted from the ancillary information included in the MODIS surface reflectance Climate Modeling Grid (MOD09CMA) for Terra, which is itself computed from the MODIS near-infrared band 18 (931-941 nm) and 19 (915-965 nm) at 1 km spatial resolution (Gao and Kaufman, 2003). "}]}, {"type": "p", "children": [{"text": "Estimating aerosol optical thickness and phase function remains the most challenging aspect of atmospheric correction. In LaSRC a simplifying assumption is that a single aerosol model (\"urban clean\", Dubovik, 2002) can adequately represent global aerosol distributions. Then the aerosol optical thickness is inverted using the two blue bands available on Landsat8/OLI (band 1 and 2) and red band for each non-water pixel that has not been flagged as cirrus. The approach relies on empirical correlation between ratios of the blue and red bands and aerosol optical thickness observed from MODIS, which has been used also as the basis for MODIS Collection 6 implementation. "}]}, {"type": "p", "children": [{"text": "The method for inverting the AOT from OLI or MSI is relatively simple if the ratio between the red and blue bands can be known for every 10-30m pixel. First, this ratio is computed at coarse resolution (0.05 degree) from 10 years of MODIS (Terra) and MISR data. The MISR AOT product is used as input to the atmospheric correction of the MODIS TOA data for each valid observation, providing the MODIS surface reflectances that can be used to derive a ratio at 0.05 degree. The data are carefully filtered for clouds and high AOT values. This data processing enables one to account for the accuracy of that ratio globally and across several years and seasons. The ratio is computed for each valid observation and subsequently fitted as a linear function of NDVI"}, {"text": "MIR", "subscript": true}, {"text": ", a vegetation index analogous to NDVI that uses the Mid-IR (2.1\u00b5m) channel instead of Red. The per-pixel ratio at the OLI/MSI resolution is then calculated from the 30m NDVI"}, {"text": "MIR", "subscript": true}, {"text": " values."}]}, {"type": "p", "children": [{"text": "The version of the LaSRC atmospheric correction code used for HLS v1.5 is derived from a C-language implementation of LaSRC version 3.5.5. used operationally for Landsat processing at USGS EROS. The only significant change from the version documented in Vermote et al. (2016) is that the aerosol optical thickness is calculated on a coarser (1km) spacing in order to speed processing time."}]}, {"type": "p", "children": [{"text": ""}]}, {"type": "p", "children": [{"text": "2.   Bi-directional Reflectance Distribution Function (BRDF) Correction"}]}, {"type": "p", "children": [{"text": "The relative view angle between a Landsat 8 OLI and Sentinel-2 MSI observation of a single ground target can be as great as $7.5+10.0 = 17.5 degrees$. This view angle difference is sufficient to generate several percent absolute reflectance difference for normal vegetation materials (Gao et al., 2009). The HLS BRDF correction attempts to normalize the surface reflectance to an optimal nadir-view value. "}]}, {"type": "p", "children": [{"text": "HLS has opted to use the c-factor technique and global coefficients provided by Roy et al. (2016) because the technique is very stable, reversible, easy to implement for operational processing and has been evaluated for Sentinel-2 data (Roy et al., 2017). The c-factor technique uses fixed BRDF coefficients for each spectral band, i.e., a constant BRDF shape, derived from a large number of pixels in the MODIS 500\u202fm BRDF product (MCD43) that are globally and temporally distributed (>15\u202fbillion\u202fpixels). The technique has been evaluated using ETM+ data off-nadir (i.e. on the overlap areas of adjacent swaths, Roy et al., 2016) and MSI data (Roy et al., 2017). The technique is applied in HLS on OLI and MSI bands equivalent to MODIS ones; MSI red-edge spectral bands are therefore not normalized. Normalized reflectance is calculated for original reflectance and a c-factor (Eq. 3). The latter is deduced (Eq. 3) from BRDF coefficients for the three kernels (isotropic, volumetric and geometric). The kernel definitions are described in the ATBD of the MOD43 product (Strahler et al., 1999), and the specific c-factor coefficients are provided in Roy et al. (2016) and Claverie et al. (2018)."}]}, {"type": "p", "children": [{"text": ""}]}, {"type": "equation", "children": [{"text": "\\rho\\left(\\lambda,\\theta^{Norm}\\right) = c\\left(\\lambda\\right)\\times\\rho\\left(\\lambda,\\theta^{sensor}\\right)"}]}, {"type": "p", "children": [{"text": ""}]}, {"type": "equation", "children": [{"text": "c\\left(\\lambda\\right) = \\frac{f_{iso}\\left(\\lambda\\right) + f_{geo}\\left(\\lambda\\right)\\times K_{geo}\\left(\\theta_{Norm}\\right) + f_{vol}\\left(\\lambda\\right) \\times K_{vol}\\left(\\theta_{Norm}\\right)}{f_{iso}\\left(\\lambda\\right) + f_{geo}\\left(\\lambda\\right)\\times K_{geo}\\left(\\theta_{Sensor}\\right) + f_{vol}\\left(\\lambda\\right) \\times K_{vol}\\left(\\theta_{Sensor}\\right)}"}]}, {"type": "p", "children": [{"text": "where $\\theta$"}, {"text": "Sensor", "subscript": true}, {"text": " refers to the sun-illumination geometry configuration (i.e., $\\theta$"}, {"text": "v", "subscript": true}, {"text": ", $\\theta$"}, {"text": "s", "subscript": true}, {"text": ", $\\Delta\\phi$) of the input data and $\\theta$"}, {"text": "Norm", "subscript": true}, {"text": " refers to the sun-illumination geometry configuration of the normalized data ($\\theta$"}, {"text": "v", "subscript": true}, {"text": " $= 0$, $\\theta$"}, {"text": "s", "subscript": true}, {"text": " = $\\theta$"}, {"text": "s_out", "subscript": true}, {"text": ", $\\Delta\\phi = 0$)."}]}, {"type": "p", "children": [{"text": "It should be noted that HLS v1.4 applied Equation 4 to correct both view and solar elevation angles, the latter an attempt to normalize for BRDF changes associated with solar elevation changes during the growing season. However, further investigation and discussions suggested that the solar elevation correction was not appropriate for the simplified C-factor formulation. As a result, HLS v1.5 only corrects for view angle differences."}]}, {"type": "p", "children": [{"text": ""}]}, {"type": "p", "children": [{"text": "3.  Bandpass Adjustments"}]}, {"type": "p", "children": [{"text": "The harmonization also requires adjustment of the small differences between the equivalent spectral bands of MSI and OLI. The OLI spectral bandpasses are used as reference, to which the MSI spectral bands are adjusted. No bandpass adjustment is defined for the (i) MSI red-edge bands (B05, B06 and B07), (ii) broad NIR band (B08), and (iii) atmospheric bands (B09 and B10). MSI bandpasses are based on the revised Sentinel-2a relative spectral responses (RSR''s) for bands 1 and 2 provided by ESA in 2017. "}]}, {"type": "p", "children": [{"text": "As described in Claverie et al. (2018), the bandpass adjustment algorithm was derived from a selection of EO-1 Hyperion hyperspectral imager spectra. 160 million per-pixel spectra were extracted from a set of 158 Hyperion scenes, distributed globally by latitude, Using the RSR for OLI and MSI, the raw (MSI) and target (OLI) spectral reflectance values were calculate for each pixel from the hyperspectral spectra. A global linear regression was then developed to transform MSI spectral reflectance to \"pseudo-OLI\" spectral reflectance. "}]}, {"type": "p", "children": [{"text": ""}]}, {"type": "p", "children": [{"text": "4. Geometric Processing"}]}, {"type": "p", "children": [{"text": "The S30 and L30 products are coregistered in the Sentinel-2 Military Grid Reference System (MGRS), partially resulting from the use of an HLS internal set of geolocation reference images when necessary. Since the processing baseline 2.04 initiated in June 2016 and up to the current 2.09 the Sentinel-2 L1C geolocation is quite stable, other than a few incidental anomalies, with long-term absolute accuracy close to 11 meters at 95$%$ for both satellites (7 Apr 2020 L1C data quality report). This accuracy is sufficient for the HLS 30-meter pixel size. When ESA applies its precisely geolocated Global Reference Image (GRI) in late 2020, the Sentinel-2 geolocation accuracy will be further improved. Therefore, HLS does not adjust the geolocation of Sentinel-2 data of this period. However, the Sentinel-2 L1C data before processing baseline 2.04 can show large geolocation error, especially a yaw angle bias apparent at the swath edges of adjacent orbits. To mitigate this problem, the HLS processing system has selected for each tile a 10-meter near-infrared summer image of minimal cloud contamination from processing baseline 2.04 to build its own internal geolocation reference images to which images of earlier processing baselines are to be registered. "}]}, {"type": "p", "children": [{"text": "The coregistration is aided by the Automated Registration and Orthorectification Package (AROP) (Gao, Wolfe and Masek, 2009), which automatically identifies tie points and fits a coordinate transformation function between a target image and the reference image for the tile. The spectral measurement in the target image is resampled with the cubic convolution technique during coregistration and saved in native 10/20/60m spatial resolutions for S10. In the subsequent production of S30, the 10/20/60m pixels of S10 are resampled to 30 meters with a simple area-weighted average. The HLS internal set of geolocation reference images will continue to be used until ESA reprocesses all images before processing baseline 2.04 to a better quality."}]}, {"type": "p", "children": [{"text": "Collection-1 Landsat-8 data often do not align with Sentinel-2 data and in general show lesser geolocation accuracy (Storey et al 2016). HLS v1.4 has applied AROP to register Landsat-8 data to the HLS internal Sentinel-2 based geolocation reference images and uses cubic convolution to resample the spectral data. With the use of ESA-provided Sentinel-2 GRI to improve the density and accuracy of Landsat ground control points, USGS will release Collection-2 Landsat data with a better geolocation accuracy in mid-2020. AROP will not be needed on Collection-2 Landsat-8 data by that time, but resampling is still necessary because the UTM coordinate origin in the Landsat-8 system corresponds to a pixel center but in the Sentinel-2 system corresponds to a pixel corner. "}]}, {"type": "p", "children": [{"text": ""}]}, {"type": "p", "children": [{"text": "5. Implementation"}]}, {"type": "p", "children": [{"text": "The HLS processing flow has been implemented on the University of Alabama/Marshall Space Flight Center (UAH/MSFC) IMPACT cloud computing environment, utilizing Amazon Web Services (AWS) services. "}]}, {"type": "p", "children": [{"text": "Input data sources include:"}]}, {"type": "p", "children": [{"text": "\u2022\tLandsat 8 Collection 2 \"Real Time\" TOA reflectance products from USGS EROS. To reduce HLS latency, IMPACT uses the \"real time\" Landsat 8 products rather than the final Tier 1 products. Users should note that this can introduce positional uncertainty in the Landsat 8 TIR observations compared to the final Tier 1 products. "}]}, {"type": "p", "children": [{"text": "\u2022\tSentinel-2 L1C TOA products. Input data for forward processing (2020 onwards) from the ESA International Hub; Input data for archival products (2015-2020) from the USGS EROS Sentinel-2 L1C mirror site. "}]}, {"type": "p", "children": [{"text": "\u2022\tLaSRC atmospheric correction inputs include ozone concentration, water vapor, and atmospheric temperature from MODIS CMA products, and surface topography (for pressure calculation) based on the Global Climate Model DEM. "}]}, {"type": "p", "children": [{"text": "Data production is kicked off daily based on new input granules from the ESA or USGS archives. HLS latency is scaled by the availability of both input TOA imagery, as well as the availability of the atmospheric correction inputs. HLS products are typically available within 2-3 days of image acquisition. "}]}]}, "scientific_theory_assumptions": {"children": [{"type": "p", "children": [{"text": "The following assumptions apply to the HLS algorithms:"}]}, {"type": "p", "children": [{"text": "LaSRC atmospheric correction assumes a plane-parallel atmosphere and Lambertian surface"}]}, {"type": "p", "children": [{"text": "BRDF correction is valid for small ranges of view angle ($<$20 degrees) near nadir"}]}, {"type": "p", "children": [{"text": "HLS does not attempt BRDF or bandpass corrections for bands that have no MODIS counterpart (e.g. Sentinel-2 red edge bands). "}]}, {"type": "p", "children": [{"text": "HLS uses \"Real Time\" Landsat 8 Collection 2 products to reduce latency; positional accuracy of the Landsat 8 TIR bands may be lower than Tier 1 data available ~2 weeks later. "}]}]}, "mathematical_theory": {"children": [{"type": "p", "children": [{"text": ""}]}]}, "mathematical_theory_assumptions": {"children": [{"type": "p", "children": [{"text": ""}]}]}, "algorithm_input_variables": [], "algorithm_output_variables": [], "algorithm_implementations": [], "algorithm_usage_constraints": {"children": [{"type": "p", "children": [{"text": ""}]}]}, "performance_assessment_validation_methods": {"children": [{"type": "p", "children": [{"text": "The HLS Project distinguishes between \u201cQuality Assurance\u201d and \u201cValidation\u201d. Quality Assurance (or QA) provides per-granule or per-pixel information on the relative quality of the observation, as a flag for users to either use or discard that observation. Validation presents a quantitative assessment of product accuracy and uncertainty against and absolute reference. "}]}, {"type": "p", "children": [{"text": "Validation", "bold": true}]}, {"type": "p", "children": [{"text": "HLS Surface Reflectance products have been validated in several ways. The LaSRC atmospheric correction has been validated by comparing Landsat 8 surface reflectance products with imagery corrected by the 6S radiative transfer model using aerosol optical thickness (AOT) derived from AERONET in-situ observations (Vermote et al., 2016). Since aerosols remain the primary source of uncertainty for retrieving surface reflectance from Landsat data, this comparison primarily tests the ability of the LaSRC algorithm to accurately retrieve AOT. Results indicate an overall uncertainty of 0.001-0.011 absolute reflectance, depending on band, with minimal dependence on target brightness (Fig. 1, Fig. 2), and similar performance for Sentinel-2. Complete results are presented in Vermote et al. (2016) and have also been included in the CEOS Atmospheric Correction Intercomparison Experiment (ACIX) (Doxani et al., 2018)."}]}, {"type": "p", "children": [{"text": ""}]}, {"type": "image-block", "children": [{"type": "img", "children": [{"text": ""}], "objectKey": "ff9636-2f71a9c0-e938-11ea-b22d-c9533a470a15.png"}, {"type": "caption", "children": [{"text": "Accuracy (bias), precision, and uncertainty of the LaSRC atmospheric correction applied to Landsat 8 and Sentinel-2 data for the red band (Band 4)"}]}]}, {"type": "p", "children": [{"text": ""}]}, {"type": "image-block", "children": [{"type": "img", "children": [{"text": ""}], "objectKey": "22c3dc-2b9f0170-e939-11ea-b22d-c9533a470a15.png"}, {"type": "caption", "children": [{"text": "Fig. YYY. LaSRC accuracy, precision, uncertainty compared to Aerojet-Derived surface reflectance for Landsat 8 OLI, data from Vermote et al. (2016). Units are absolute reflectance x10000 (e.g., 10=1 percent reflectance or 0.01)"}]}]}, {"type": "p", "children": [{"text": "A second validation exercise focused on using ground-based albedometer networks (SURFRAD and OZFLUX) to compare HLS nadir-adjusted reflectance to observed albedo. There are several factors that must be considered when performing this comparison: "}]}, {"type": "ul", "children": [{"type": "li", "children": [{"type": "p", "children": [{"text": "Comparing directional reflectance with albedo requires a hemispheric integration using a known bi-directional reflectance distribution function (BRDF), as well as a spectral integration to compare the relatively narrow HLS band passes with the broader channels of the albedometer instruments. "}]}]}]}, {"type": "ul", "children": [{"type": "li", "children": [{"type": "p", "children": [{"text": "The view footprint of the SURFAD and OZFLUX instruments is much smaller than an HLS 30-meter pixel, so local heterogeneity beyond the albedometer field of view will affect the comparison. "}]}]}]}, {"type": "p", "children": [{"text": "Results are presented in Franch et al. (2019) (Fig. 3). Using sites in both the US and Australia, albedo estimates using S30 and L30 products as input provide comparable RMSE (0.015 \u2013 0.03 albedo). "}]}, {"type": "p", "children": [{"text": "[Figure 3 here]"}]}, {"type": "p", "children": [{"text": "Finally, Claverie et al. (2018) presented a theoretical error budget for the HLS product derived from published assessments of component errors from each of the algorithms (atmospheric correction, BRDF adjustment, spectral band pass correction). Rolling up the published component errors and assuming they are independent (e.g. total error is the root sum square of component errors) indicates per-band uncertainty of 0.01 to 0.02 absolute reflectance. "}]}, {"type": "p", "children": [{"text": "It should be noted that the above analyses compare HLS products to absolute reflectance or albedo estimates. Users may care more about the temporal stability (comparability) of reflectance from the S30 and L30 products, rather than the absolute error. Time series analysis over stable (e.g. invariant desert) sites are ongoing, but short-term variability from these sites is generally less than 0.5$%$ absolute reflectance over a period of days."}]}, {"type": "p", "children": [{"text": "Quality Assurance (QA)", "bold": true}]}, {"type": "p", "children": [{"text": "Per-pixel QA information is included with each v1.5 HLS S30 or L30 image via the QA layer. Clouds and cloud shadow are identified using the Fmask 4.0 algorithm published by Zhu et al (2015). "}]}, {"type": "p", "children": [{"text": "In previous HLS versions, per-granule QA statistics were derived by comparing aggregated HLS reflectance values to near-simultaneous, cloud-free MODIS CMG reflectances (Claverie et al., 2018, Fig. 1). In general, this approach showed that HLS reflectance values were consistent with MODIS reflectance values, except for those cases where HLS cloud masking failed. Version 1.5 has discontinued the per-granule MODIS comparisons as the approach has not proved critical for flagging \u201cpoor\u201d granules. "}]}]}, "performance_assessment_validation_uncertainties": {"children": [{"type": "p", "children": [{"text": ""}]}]}, "performance_assessment_validation_errors": {"children": [{"type": "p", "children": [{"text": ""}]}]}, "data_access_input_data": [], "data_access_output_data": [{"url": "https://lpdaac.usgs.gov/products/hlss30v015/", "description": "HLSS30 dataset landing page"}, {"url": "https://lpdaac.usgs.gov/products/hlsl30v015/", "description": "HLSL30 dataset landing page."}], "data_access_related_urls": [{"url": "https://GIBS_layer_S30.com", "description": "HLS S30 imagery in GIBS. This link can be used to view HLS S30 imagery in the GIBS Worldview client (will be updated once data becomes available)"}, {"url": "https://search.earthdata.nasa.gov/search/granules?p=C1711924822-LPCLOUD&pg[0][gsk]=-start_date&q=HLS&m=-0.140625!0!2!1!0!0%2C2&tl=1582733901!4!!", "description": "S30 Data Access URL. This URL can be used to directly access HLS S30 data"}, {"url": "https://search.earthdata.nasa.gov/search/granules?p=C1711972753-LPCLOUD&pg[0][gsk]=-start_date&q=HLS&m=-0.140625!0!2!1!0!0%2C2&tl=1582733901!4!!", "description": "L30 Data Access URL. This URL can be used to directly access HLS L30 data"}, {"url": "https://cmr.earthdata.nasa.gov/search/concepts/C1711924822-LPCLOUD.html?token=229aabe8b195cfe39ee6c58e45885832f98baeb7bf46463e93915c88c26d779a:OLpAZlE4HqIOMr0TYqg7UQ", "description": "S30 Collection metadata URL. This URL can be used to directly access the collection level metadata record for the S30 data product"}, {"url": "https://cmr.earthdata.nasa.gov/search/concepts/C1711972753-LPCLOUD.html?token=4d94c4278610e8bec249b1cfd623af4ff10867f54bdc6588979e4ccceb5faf51:OLpAZlE4HqIOMr0TYqg7UQ", "description": "L30 Collection metadata URL. This URL can be used to directly access the collection level metadata record for the L30 data product"}, {"url": "https://GIBS_layer_L30.com", "description": "HLS L30 imagery in GIBS. This link can be used to view HLS L30 imagery in the GIBS Worldview client (will be updated once data becomes available)"}], "journal_discussion": {"children": [{"type": "p", "children": [{"text": ""}]}]}, "journal_acknowledgements": {"children": [{"type": "p", "children": [{"text": ""}]}]}, "publication_references": [{"id": "dce6ecc7", "authors": "Martin Claverie, Junchang Ju, Jeffrey G. Masek, Jennifer L. Dungan, Eric F. Vermote, Jean-Claude Roger, Sergii V. Skakun, Christopher Justice", "title": "The Harmonized Landsat and Sentinel-2 surface reflectance data set", "series": "Remote Sensing of Environment", "edition": "", "volume": "219", "issue": "", "publication_place": "", "publisher": "", "pages": "", "isbn": "", "year": "2018"}, {"id": "1442b70f", "authors": "Eric Vermote, Chris Justice, Martin Claverie, Belen Franch", "title": "Preliminary analysis of the performance of the Landsat 8/OLI land surface reflectance product", "series": "Remote Sensing of Environment", "edition": "", "volume": "185", "issue": "", "publication_place": "", "publisher": "", "pages": "", "isbn": "", "year": "2016"}, {"id": "9fcc9dd6", "authors": "Belen Franch, Eric Vermote, Sergii Skakun, Jean-Claude Roger, Jeffrey Masek, Junchang Ju, Jose Luis Villaescusa-Nadal, Andres Santamaria-Artigas", "title": "A method for Landsat and Sentinel 2 (HLS) BRDF normalization", "series": "Remote Sensing", "edition": "", "volume": "11", "issue": "", "publication_place": "", "publisher": "", "pages": "", "isbn": "", "year": "2019", "report_number": "", "doi": "https://doi.org/10.3390/rs11060632", "online_resource": "", "other_reference_details": ""}]}', '{"citation": "incomplete", "contacts": "incomplete", "introduction": "incomplete", "historical_perspective": "incomplete", "scientific_theory": "incomplete", "mathematical_theory": "incomplete", "input_variables": "incomplete", "output_variables": "incomplete", "constraints": "incomplete", "validation": "incomplete", "algorithm_implementations": "incomplete", "data_access_input_data": "incomplete", "data_access_output_data": "incomplete", "data_access_related_urls": "incomplete", "context_background": "incomplete"}', NULL, NULL, 'ce55b12c-b868-4e37-b045-1777028d92e6', '2021-07-09 15:47:02.086481+00', 'ce55b12c-b868-4e37-b045-1777028d92e6', '2022-01-06 18:01:51.835931+00', '', '{"creators": "", "editors": "", "title": "", "series_name": "", "release_date": "", "release_place": "", "publisher": "", "version": "", "issue": "", "additional_details": "", "online_resource": ""}', 'ce55b12c-b868-4e37-b045-1777028d92e6', '{}', '{}', 'DRAFT', 'NO_PUBLICATION', '{"journal_editor": "Chelle Gentemann"}', '{}');
